import React,{useState} from 'react'

const Users = () => {
    const [person, setperson] = useState({
        fname:"",lname:""
    })
    const [fname, setfname] = useState("")

    const handleChange=(event)=>{
        // console.log(event.target.value)
        // setfname(event.target.value)

        var variableName=event.target.name;
        // console.log(variableName)
        var newPerson={...person}
        newPerson=[variableName]=event.target
        console.log(variableName)

    }
  return (
    <div>
    <label htmlFor="">First Name:</label>
    <input type="text" name="fname" value={fname} onChange={(event)=>{handleChange(event)}}/>
    <br /><label htmlFor="Lname">LastName:</label>
    <input type="text" name="Lname" value={person.lname} onChange={(event)=>{handleChange(event)}} />
    <br /><button>Add Users</button>
    </div>
  )
}

export default Users;
