import React, { useState } from 'react'

const Users = () => {
    const [person, setperson] = useState({
        fname: "", lname: ""
    })
    // const [fname, setfname] = useState("")

    const handleChange = (event) => {
        // console.log(event.target.value)
        // setfname(event.target.value)

        var variableName = event.target.name;
        // console.log(variableName)
        var newPerson = { ...person }
        newPerson = [variableName] = event.target.value;
        setperson(newPerson)
        // console.log(variableName)

    }

    const addPerson = () => {
        console.log(person)
    }


    return (
        <div>
            <label htmlFor="fname">First Name:</label>
            <input type="text" name="fname" value={person.fname} onChange={(event) => { handleChange(event) }} />
            <br /> <label htmlFor="lname">LastName:</label>
            <input type="text" name="lname" value={person.lname} onChange={(event) => { handleChange(event) }} />
            <label htmlFor="">Date Of Birth</label>br
            <input type="text" name="DateOfBirth" value={person.dateofbirth} onChange={(event) => { handleChange(event) }}/>
            <br /><button onClick={addPerson}>Add Users</button>
        </div>
    )
}

export default Users;
